mapper.update:
UPDATE blog_comment
SET like_count = like_count+1
where id=#{targetId,jdbcType=INTEGER}

mapper.select:
SELECT

FROM blog_comment
WHERE comment_status =0 AND id=#{id,jdbcType=INTEGER}

SELECT t.id, t.target_id as targetId, t.email, u1.user_name as userName, u2.user_name as replyUserName, t.reply_email as replyEmail, t.content, t.like_count as likeCount, t.create_time as createTime, t.comment_status as commentStatus, t.session_id as sessionId from blog_comment t LEFT JOIN user u1 ON t.email = u1.email LEFT JOIN user u2 ON t.reply_email = u2.email
and t.target_id = #{targetId}
and t.session_id = #{sessionId}
and t.email = #{email}
and t.reply_email = #{replyEmail}
and t.comment_status = #{commentStatus}
